codec = []

def isstart(code):
    if len(code) < 4:
        return False
    for c in code:
        if code.count(c) > 1:
            return False
    return True


def ismessage(code):
    if len(code) < 14:
        return False
    for c in code:
        if code.count(c) > 1:
            return False
    return True


n = 0

for c in input:
    codec.append(c)
    if len(codec) > 4:
        codec.pop(0)
    if isstart(codec):
        print("Part one:", n+1)
        break
    n = n +1


